$(function () {
    $("#save").click(function () {
        checkRequired("#working_area") && $.ajax({
            type: "post",
            url: "../../api/admin/pwd_policy/",
            data: getFormJson($("#working_area form"))
        }).done(function (a) {
            if (0 === a.code)sysAlert("success", "\u4fdd\u5b58\u6210\u529f\uff01", "\u4fdd\u5b58\u5bc6\u7801\u7b56\u7565\u6210\u529f\u3002"); else {
                debugger;
                sysAlert("error", "\u9519\u8bef\uff01", "\u4fdd\u5b58\u5bc6\u7801\u7b56\u7565\u5931\u8d25\u3002[" + a.msg + " " + a.code + "]")
            }
        }).fail(function (a) {
            sysAlert("error", "\u9519\u8bef\uff01",
                "\u4fdd\u5b58\u5bc6\u7801\u7b56\u7565\u5931\u8d25\u3002[" + a.status + " " + a.statusText + "]")
        })
    })
});
